// toJson

const baseConfig = require('./webpack.config.base')
const path = require('path')
const webpack = require('webpack')
const merge = require('webpack-merge')
const VueServerPlugin = require('vue-server-renderer/server-plugin')
const webpackNodeExternals = require('webpack-node-externals')

const plugins = baseConfig.plugins.concat([
  // 将打包后的结果变成.json文件，且默认文件名为vue-ssr-client-manifest.json
  new VueServerPlugin(),
  new webpack.DefinePlugin({
    'process.env.NODE_ENV': 'development',
    'process.env.VUE_ENV': 'server'
  })
])
module.exports = merge(baseConfig, {
  // 不打包node_module包，因为程序是运行在node端，可以直接引入
  externals: [webpackNodeExternals()],
  target: 'node',
  devtool: 'source-map',
  // entry: '../client/server-entry.js'   用这种方式会有找不到文件的提示，应该和打包路径有关
  entry: path.join(__dirname, '../client/server-entry.js'),

  // 此处告知 server bundle 使用 Node 风格导出模块(Node-style exports)
  output: {
    filename: 'server-bundle.js',
    libraryTarget: 'commonjs2',
    path: path.join(__dirname, '../dist')
  },
  plugins
})

const path = require('path')
// 引入vue-loader插件
const VueLoaderPlugin = require('vue-loader/lib/plugin')

module.exports = {
  entry: path.join(__dirname, '../client/client-entry'),
  output: {
    filename: 'bundle.js',
    publicPath: '/public/'
  },
  module: {
    rules: [//配置vue
      {
          test: /\.vue$/,
          use: ['vue-loader']
      }
    ]  
  },
  plugins: [
    // vue加载器插件
    new VueLoaderPlugin()
  ]
}

// import Vue from 'vue'
const Vue = require('vue')
const { createRouter } = require('./router/router')

module.exports.createApp = () => {
  const router = createRouter()
  const app = new Vue({
    router,
    template: `<div>这是服务端用的vue模板 
      <router-link to="/about">关于我们</router-link>
      <router-link to="/menu">菜单</router-link>
      <router-view></router-view>
    </div>`
   })
   return { app, router }
}

const Router = require('vue-router')
const Vue = require('vue')

Vue.use(Router)
module.exports.createRouter = () => {
  return new Router({
    mode: 'history',
    routes: [
      { path: '/', component: { template: '<div>首页</div>'} },
      { path: '/menu', component: { template: '<div>菜单</div>'} },
      { path: '/about', component: { template: '<div>关于我们</div>'}}
    ]
  })
}
